    }

    h1{
        color: #8d31ce;
    }
`}</style>
        </div>
    </Layout>
)

Index.getInitialProps = async function(){
    const res = await fetch
    ('https://api.coindesk.com/v1/bpi/currentprice.json')
    const data = await res.json()

    return {
        bpi: data.bpi
    };
}

export default Index
